import React, { useState, useEffect } from "react";
import { Helmet } from "react-helmet";
import axios from "axios";
import { Link } from "react-router-dom";
import { connect } from "react-redux";

const Brought = ({ match, isAuthenticated, email }) => {
    const [listing, setListing] = useState({});
    const [realtor, setRealtor] = useState({});
    const [buyer, setBuyer] = useState("");

    useEffect(() => {
        const slug = match.params.id;

        const config = {
            headers: {
                Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
        };

        axios
            .get(
                `${process.env.REACT_APP_API_URL}/api/listings/${slug}`,
                config
            )
            .then((res) => {
                setListing(res.data);
            })
            .catch((err) => {
                console.log(err);
            });
    }, [match.params.id]);

    useEffect(() => {
        const id = listing.realtor;

        const config = {
            headers: {
                Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
        };

        if (id) {
            axios
                .get(
                    `${process.env.REACT_APP_API_URL}/api/realtors/${id}/`,
                    config
                )
                .then((res) => {
                    setRealtor(res.data);
                })
                .catch((err) => {
                    console.log(err);
                });
        }
    }, [listing.realtor]);

    useEffect(() => {
        const value = email;

        const config = {
            headers: {
                Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
        };
        if (isAuthenticated) {
            axios
                .get(`${process.env.REACT_APP_API_URL}/api/orders/`, config)
                .then((res) => {
                    const x = res.data.results.filter((val) => {
                        return val.buyer == value;
                    });
                    setBuyer(x[0].buyer);
                })
                .catch((err) => {
                    console.log(err);
                });
        }
    }, [email]);

    return (
        <>
            <Helmet>
                <title>Real Estate - {`${listing.title}`}</title>
                <meta name="description" content="Listing detail" />
            </Helmet>
            {email === buyer ? (
                <>
                    <header className="heading-component">
                        <h1 className="font-weight-bold">{listing.title}</h1>
                        <h6 className="">
                            {listing.city}, {listing.state}, {listing.zipcode}
                        </h6>
                    </header>
                    <div className="container mt-3">
                        <div className="link-bar">
                            <h4>
                                <Link exact to="/">
                                    Home
                                </Link>{" "}
                                / Your House
                            </h4>
                        </div>
                        <div className="row my-3">
                            <div className="col-md-9 col-12">
                                <figure>
                                    <img
                                        src={listing.photo_main}
                                        alt="Main Tasbir"
                                        width="100%"
                                        height="auto"
                                    />
                                </figure>
                                <div className="row detail font-weight-bold">
                                    <div className="col-md-6">
                                        <ul>
                                            <li>
                                                <p className="m-1">
                                                    Price: ${listing.price}
                                                </p>
                                            </li>
                                            <li>
                                                <p className="m-1">
                                                    Bedrooms: {listing.bedrooms}
